    # --- Replace the existing start_drawing_process method with this one ---
    def start_drawing_process(self, commands_to_send: List[Tuple]):
        """Sends drawing commands and signature to the robot sequentially (RUNS IN THREAD)."""
        total_drawing_commands = len(commands_to_send)
        success = True
        last_sent_point = None

        if not self.connected or not self.socket:
            logging.error("Drawing started but not connected.")
            self.window.after(0, lambda: messagebox.showerror("Error", "Connection lost before drawing started."))
            self.window.after(0, self.drawing_options_page) # Go back
            self.drawing_in_progress = False
            return

        # --- Generate Signature Commands ---
        # Use the global SIGNATURE_POINTS constant defined above
        signature_commands = create_signature_commands(SIGNATURE_POINTS)
        total_signature_commands = len(signature_commands)
        # Calculate total commands for progress bar
        total_commands = total_drawing_commands + total_signature_commands

        # --- Update Progress Page ---
        # Show progress page with total combined commands
        self.window.after(0, lambda: self.show_drawing_progress_page(total_commands)) # [cite: 185]
        self.window.after(0, lambda: self.update_drawing_status(0, total_commands, "Starting Drawing")) # [cite: 187]

        current_command_global_index = 0 # Track overall progress

        try:
            # --- Send Drawing Commands ---
            logging.info(f"Starting to send {total_drawing_commands} drawing commands.")
            for i, (x, z, y) in enumerate(commands_to_send):
                current_command_global_index = i + 1
                command_str = f"{x:.2f},{z},{y:.2f}" # Format for robot [cite: 191]
                logging.debug(f"Sending drawing command {current_command_global_index}/{total_commands}: {command_str}")

                # --- Robot Communication Protocol ---
                # 1. Send Command
                if not self.send_message_internal(command_str): #
                    success = False
                    self.window.after(0, lambda idx=current_command_global_index: self.update_drawing_status(idx, total_commands, "Drawing Send Failed")) # [cite: 192]
                    break

                # 2. Wait for Receipt 'R'
                response_r = self.receive_message_internal(timeout=10.0) #
                if response_r != "R":
                    logging.error(f"Robot did not confirm receipt (R) for drawing command {current_command_global_index}, got '{response_r}'. Stopping.") # [cite: 193]
                    success = False
                    self.window.after(0, lambda idx=current_command_global_index, r=response_r: self.update_drawing_status(idx, total_commands, f"Draw Error: No 'R' (Got {r})")) # [cite: 194]
                    break

                # 3. Wait for Done 'D'
                response_d = self.receive_message_internal(timeout=60.0) # Longer timeout for move completion [cite: 195]
                if response_d != "D":
                    logging.error(f"Robot did not confirm completion (D) for drawing command {current_command_global_index}, got '{response_d}'. Stopping.") # [cite: 195]
                    success = False
                    self.window.after(0, lambda idx=current_command_global_index, d=response_d: self.update_drawing_status(idx, total_commands, f"Draw Error: No 'D' (Got {d})")) # [cite: 196]
                    break
                # --- End Protocol ---

                # Update GUI progress
                self.window.after(0, lambda idx=current_command_global_index: self.update_drawing_status(idx, total_commands)) # [cite: 197]
                last_sent_point = (x, z, y)

            # --- Send Signature Commands (only if drawing was successful so far) ---
            if success:
                logging.info(f"Drawing commands sent successfully. Starting to send {total_signature_commands} signature commands.")
                self.window.after(0, lambda idx=current_command_global_index: self.update_drawing_status(idx, total_commands, "Starting Signature"))
                for j, (x, z, y) in enumerate(signature_commands):
                    current_command_global_index = total_drawing_commands + j + 1
                    command_str = f"{x:.2f},{z},{y:.2f}" # Format signature command
                    logging.debug(f"Sending signature command {current_command_global_index}/{total_commands}: {command_str}")

                    # --- Robot Communication Protocol (repeated for signature) ---
                    # 1. Send Command
                    if not self.send_message_internal(command_str):
                        success = False # Mark success as false for the final move logic
                        self.window.after(0, lambda idx=current_command_global_index: self.update_drawing_status(idx, total_commands, "Signature Send Failed"))
                        break
                    # 2. Wait for Receipt 'R'
                    response_r = self.receive_message_internal(timeout=10.0)
                    if response_r != "R":
                        logging.error(f"Robot did not confirm receipt (R) for signature command {current_command_global_index}, got '{response_r}'. Stopping.")
                        success = False
                        self.window.after(0, lambda idx=current_command_global_index, r=response_r: self.update_drawing_status(idx, total_commands, f"Sig Error: No 'R' (Got {r})"))
                        break
                    # 3. Wait for Done 'D'
                    response_d = self.receive_message_internal(timeout=60.0)
                    if response_d != "D":
                        logging.error(f"Robot did not confirm completion (D) for signature command {current_command_global_index}, got '{response_d}'. Stopping.")
                        success = False
                        self.window.after(0, lambda idx=current_command_global_index, d=response_d: self.update_drawing_status(idx, total_commands, f"Sig Error: No 'D' (Got {d})"))
                        break
                    # --- End Protocol ---

                    # Update GUI progress for signature
                    self.window.after(0, lambda idx=current_command_global_index: self.update_drawing_status(idx, total_commands))
                    last_sent_point = (x, z, y) # Update last point

        except Exception as e:
            logging.error(f"Unexpected error during drawing/signature process: {e}") # [cite: 198]
            success = False
            # Update status with error, using the latest global index
            self.window.after(0, lambda idx=current_command_global_index: self.update_drawing_status(idx, total_commands, f"Error: {e}")) # [cite: 198]

        finally:
            # --- Send Final Position Command ---
            # This runs AFTER both drawing and signature attempts
            if success: # Only go home if drawing AND signature finished ok
                logging.info("Drawing and signature commands sent successfully.")
                self.window.after(0, lambda: self.update_drawing_status(total_commands, total_commands, "Signature Done. Moving to final position...")) #
                # Use the FINAL_ROBOT_POSITION constant
                final_x, final_z, final_y = FINAL_ROBOT_POSITION # [cite: 114]
                command_str_final = f"{final_x:.3f},{final_z:.3f},{final_y:.3f}" # Format final command
                logging.info(f"Sending final position command: {command_str_final}")

                # --- Robot Communication Protocol (for final move) ---
                if self.send_message_internal(command_str_final):
                    response_r_final = self.receive_message_internal(timeout=10.0)
                    if response_r_final == "R":
                        response_d_final = self.receive_message_internal(timeout=60.0) # Longer timeout for final move
                        if response_d_final == "D":
                            logging.info("Robot reached final position.") # [cite: 202]
                            self.window.after(0, lambda: self.status_label.config(text="Drawing & Signature Complete. Robot at final position.")) # [cite: 202]
                        else:
                            logging.error(f"Robot didn't confirm final move completion (D), got '{response_d_final}'") # [cite: 203]
                            self.window.after(0, lambda: self.status_label.config(text="Drawing & Signature Complete. Final move confirmation failed.")) #
                    else:
                        logging.error(f"Robot didn't confirm final move receipt (R), got '{response_r_final}'") # [cite: 204]
                        self.window.after(0, lambda: self.status_label.config(text="Drawing & Signature Complete. Final move send failed (No 'R').")) # [cite: 204]
                else:
                    logging.error("Failed to send final position command.") # [cite: 205]
                    self.window.after(0, lambda: self.status_label.config(text="Drawing & Signature Complete. Failed to send final move command.")) # [cite: 205]
            else:
                # Update status if drawing or signature failed
                logging.warning("Drawing or signature process failed. Skipping final move.")
                self.window.after(0, lambda: self.status_label.config(text="Drawing/Signature stopped due to error.")) # [cite: 206]

            # --- Cleanup ---
            logging.info("Drawing process finished (success or failure). Resetting state.")
            self.drawing_in_progress = False
            self.selected_commands = None
            # Go back to the drawing options page after a short delay
            self.window.after(2000, self.drawing_options_page) # Wait 2s before going back [cite: 207]

    # --- Internal Socket Methods (without GUI popups) ---
    def send_message_internal(self, message: str) -> bool:
        """ Sends message without triggering GUI popups on error. Returns success status. """
        if not self.connected or not self.socket: return False
        try:
            self.socket.sendall(message.encode('utf-8'))
            logging.debug(f"Sent (internal): {message}")
            return True
        except (socket.error, ConnectionResetError, socket.timeout) as e:
            logging.error(f"Send error (internal): {e}")
            self.close_socket() # Close broken socket
            return False

    def receive_message_internal(self, timeout=10.0) -> Optional[str]:
         """ Receives message without triggering GUI popups on error. Returns message or None. """
         if not self.connected or not self.socket: return None
         try:
             self.socket.settimeout(timeout)
             data = self.socket.recv(1024)
             self.socket.settimeout(None) # Reset timeout
             decoded_data = data.decode('utf-8').strip()
             logging.debug(f"Received (internal): {decoded_data}")
             return decoded_data
         except socket.timeout:
             logging.error(f"Timeout receiving message (internal)")
             # Don't necessarily close socket on timeout, maybe robot is just slow
             return None
         except (socket.error, ConnectionResetError) as e:
             logging.error(f"Receive error (internal): {e}")
             self.close_socket() # Close broken socket
             return None


    # --- Connection Handling ---
    def establish_connection(self):
        """Attempt connection (modified to use internal methods)."""
        if hasattr(self, 'connect_button'): self.connect_button.config(state=tk.DISABLED)
        host, port = (SIMULATION_HOST, SIMULATION_PORT) if self.connection_var.get() == "simulation" else (REAL_ROBOT_HOST, REAL_ROBOT_PORT)

        def connection_attempt():
            try:
                self.close_socket() # Ensure clean start
                self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
                self.socket.settimeout(5)
                self.socket.connect((host, port))
                self.socket.setsockopt(socket.SOL_SOCKET, socket.SO_KEEPALIVE, 1)
                self.socket.settimeout(None) # Default to blocking for operations
                logging.info(f"Connected to {host}:{port}")
                self.connected = True
                self.window.after(0, lambda: self.handle_connection_result(True))
            except (socket.error, socket.timeout, ConnectionRefusedError) as e:
                logging.error(f"Connection error: {e}")
                self.connected = False
                self.close_socket()
                self.window.after(0, lambda: self.handle_connection_result(False))
        threading.Thread(target=connection_attempt, daemon=True).start()

    def handle_connection_result(self, connected):
        """Handle connection result."""
        if hasattr(self, 'connect_button') and self.connect_button.winfo_exists():
            self.connect_button.config(state=tk.NORMAL)
        if connected:
            self.connection_established = True
            self.drawing_options_page() # Go to drawing options
        else:
            messagebox.showerror("Connection Failed", "Failed to establish connection.")

    def close_socket(self):
        """Close socket cleanly."""
        # (Implementation remains the same as previous version)
        if self.socket:
            try:
                self.socket.shutdown(socket.SHUT_RDWR)
            except (socket.error, OSError): pass # Ignore errors if already closed
            finally:
                 try: self.socket.close()
                 except (socket.error, OSError): pass
                 self.socket = None
                 self.connected = False
                 self.connection_established = False
                 logging.info("Connection closed")
        else:
             self.connected = False
             self.connection_established = False

    def close_and_return_main(self):
         """Close connection and go to main page."""
         self.close_socket()
         self.main_page()

    # --- Utility Methods ---
    def clear_frame(self):
        """Clear all widgets from the main frame."""
        # Stop camera if running when clearing frame
        if self.camera_running:
            self.stop_camera_feed()
        # Destroy widgets
        for widget in self.main_frame.winfo_children():
            widget.destroy()
        # Reset references
        self.camera_frame_label = None
        self.capture_button = None
        self.camera_back_button = None
        self.progress_bar = None
        self.status_label = None


    @staticmethod
    def run_script(script_path: str) -> bool:
        """Run a Python script (kept for calibration)."""
        # (Implementation remains the same)
        if not os.path.exists(script_path):
             logging.error(f"Script not found: {script_path}")
             return False
        try:
            logging.info(f"Running script: {script_path}")
            result = os.system(f'python "{script_path}"')
            if result != 0: logging.error(f"Script {script_path} failed with exit code {result}")
            return result == 0
        except Exception as e:
            logging.error(f"Error running script {script_path}: {e}")
            return False

    def on_window_close(self):
        """Handle window close event."""
        logging.info("Window close requested.")
        self.stop_camera_feed() # Ensure camera stops
        self.close_socket()     # Ensure socket closes
        self.window.destroy()


